package thirdpackage;

public class LearningOperators {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 19;
		int b = 80;
		int c = a + b ;
		c++;
		c--;
		--c;
		c++;
		c = a * b;
		c = a % b;
		c = a / b;
		System.out.println("the value of c is : " + c);
	}

}
